package com.androidtechguru.neosoft.task.data.diimport com.androidtechguru.neosoft.task.data.repository.DataRepositoryImplimport com.androidtechguru.neosoft.task.data.source.LocalDataSourceimport com.androidtechguru.neosoft.task.domain.repository.DataRepositoryimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponent@Module@InstallIn(SingletonComponent::class)object DataModule {    @Provides    fun provideLocalDataSource(): LocalDataSource = LocalDataSource()        @Provides    fun provideDataRepository(        localDataSource: LocalDataSource    ): DataRepository = DataRepositoryImpl(localDataSource)}