package com.androidtechguru.neosoft.task.data.repositoryimport com.androidtechguru.neosoft.task.data.model.ImageEntityimport com.androidtechguru.neosoft.task.data.model.ItemEntityimport com.androidtechguru.neosoft.task.data.source.LocalDataSourceimport com.androidtechguru.neosoft.task.domain.repository.DataRepositoryimport com.androidtechguru.neosoft.task.domain.model.Imageimport com.androidtechguru.neosoft.task.domain.model.Itemimport javax.inject.Injectclass DataRepositoryImpl @Inject constructor(    private val localDataSource: LocalDataSource) : DataRepository {    override suspend fun getImages(): List<Image> {        return localDataSource.getImages().map { it.toDomain() }    }        override suspend fun getItems(): List<Item> {        return localDataSource.getItems().map { it.toDomain() }    }}fun ItemEntity.toDomain() = Item(title, subtitle, imageUrl)fun ImageEntity.toDomain() = Image(url)