package com.androidtechguru.neosoft.task.data.sourceimport com.androidtechguru.neosoft.task.Rimport com.androidtechguru.neosoft.task.data.model.ImageEntityimport com.androidtechguru.neosoft.task.data.model.ItemEntityimport javax.inject.Injectimport kotlin.random.Randomclass LocalDataSource @Inject constructor() {    fun getItems(): List<ItemEntity> {        return generateDummyItemListForPage()    }        fun getImages(): List<ImageEntity> {        return generateDummyImageListForPage()    }        private fun generateDummyImageListForPage(): List<ImageEntity> {        val imageList = mutableListOf<ImageEntity>()        repeat(Random.nextInt(2, 50)) {            imageList.add(                ImageEntity(getDummyImageFromDrawable().toString())            )        }        return imageList    }        private fun generateDummyItemListForPage(): List<ItemEntity> {        // Generate dummy list items based on page index...                val items = mutableListOf<ItemEntity>()        for (item in 1..Random.nextInt(1, 50)) {            val title =                if (Random.nextBoolean()) "List Item Title : $item" else " Title: $item - ${generateRandomFruit()}"            val subtitle =                if (Random.nextBoolean()) "subtitle - ${generateRandomFruit()}" else "${generateRandomFruit()} -> ${generateRandomFruit()}"            items.add(                ItemEntity(                    title = title,                    subtitle = subtitle,                    imageUrl = getDummyImageFromDrawable().toString()                )            )        }        return items    }        private fun generateRandomFruit(): String {        val dummyFruitList =            listOf("apple", "banana", "orange", "blueberry", "kiwi", "mango", "papaya", "pineapple")        return dummyFruitList.shuffled()[Random.nextInt(0,            dummyFruitList.size)]    }        private fun getDummyImageFromDrawable(): Int {        return when (Random.nextInt(0, 10)) {            0 -> R.drawable.md            1 -> R.drawable.android            2 -> R.drawable.layout            3 -> R.drawable.hello            4 -> R.drawable.kf            5 -> R.drawable.flower            6 -> R.drawable.devops            7 -> R.drawable.bird            8 -> R.drawable.bloom            else -> R.drawable.android        }    }}