package com.androidtechguru.neosoft.task.domain.usecaseimport com.androidtechguru.neosoft.task.domain.model.Itemimport com.androidtechguru.neosoft.task.domain.repository.DataRepositoryimport javax.inject.Injectclass GetItemsUseCase @Inject constructor(    private val itemRepository: DataRepository) {    suspend operator fun invoke(page: Int): List<Item> {        return itemRepository.getItems()    }}