@file:OptIn(ExperimentalMaterial3Api::class)package com.androidtechguru.neosoft.task.uiimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.wrapContentHeightimport androidx.compose.material3.ExperimentalMaterial3Apiimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.ModalBottomSheetimport androidx.compose.material3.Textimport androidx.compose.material3.rememberModalBottomSheetStateimport androidx.compose.runtime.Composableimport androidx.compose.runtime.rememberCoroutineScopeimport androidx.compose.ui.Modifierimport androidx.compose.ui.text.style.TextAlignimport androidx.compose.ui.unit.dpimport com.androidtechguru.neosoft.task.viewmodel.MainViewModel@Composablefun BottomSheetDialog(viewModel: MainViewModel) {    val sheetState = rememberModalBottomSheetState(skipPartiallyExpanded = true)        ModalBottomSheet(        onDismissRequest = {            viewModel.closeBottomSheet()        },        sheetState = sheetState,        shape = MaterialTheme.shapes.large    ) {        Column(modifier = Modifier            .padding(16.dp)            .fillMaxWidth()            .wrapContentHeight()) {            Text("Statistics",                textAlign = TextAlign.Center,                modifier = Modifier.fillMaxWidth())            // Display statistics DATA...            StatisticsReport(viewModel)        }    }}