package com.androidtechguru.neosoft.task.ui.componentsimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.material3.Textimport androidx.compose.material3.TextFieldimport androidx.compose.runtime.*import androidx.compose.ui.Modifierimport com.androidtechguru.neosoft.task.viewmodel.MainViewModel@Composablefun SearchBar(viewModel: MainViewModel) {    var query by remember { mutableStateOf("") }        TextField(        value = query,        onValueChange = {            query = it            viewModel.onSearchQueryChanged(query)        },        modifier = Modifier.fillMaxWidth(),        placeholder = {            Text(text = "Search")        }    )}