@file:OptIn(ExperimentalFoundationApi::class)package com.androidtechguru.neosoft.task.ui.screensimport androidx.compose.foundation.ExperimentalFoundationApiimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.heightimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.lazy.LazyColumnimport androidx.compose.foundation.lazy.rememberLazyListStateimport androidx.compose.foundation.shape.CircleShapeimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.MoreVertimport androidx.compose.material3.FloatingActionButtonimport androidx.compose.material3.Iconimport androidx.compose.material3.Scaffoldimport androidx.compose.runtime.Composableimport androidx.compose.runtime.collectAsStateimport androidx.compose.runtime.getValueimport androidx.compose.ui.Modifierimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dpimport androidx.hilt.navigation.compose.hiltViewModelimport com.androidtechguru.neosoft.task.ui.BottomSheetDialogimport com.androidtechguru.neosoft.task.ui.components.Carouselimport com.androidtechguru.neosoft.task.ui.components.ListScreenimport com.androidtechguru.neosoft.task.ui.components.SearchBarimport com.androidtechguru.neosoft.task.ui.theme.FABColorimport com.androidtechguru.neosoft.task.viewmodel.MainViewModel@Preview@Composablefun MainScreen(viewModel: MainViewModel = hiltViewModel()) {    val isBottomSheetVisible by viewModel.isBottomSheetVisible.collectAsState()    val carouselImages by viewModel.images.collectAsState()    val state = rememberLazyListState()        Scaffold(        floatingActionButton = {            FloatingActionButton(onClick = { viewModel.onFabClicked() },                containerColor = FABColor,                shape = CircleShape) {                Icon(Icons.Default.MoreVert, contentDescription = null)            }        },        content = { paddingValues ->            LazyColumn(modifier = Modifier                .fillMaxSize()                .padding(paddingValues)                .padding(16.dp),                state = state) {                item {                    Carousel(carouselImages) { currentPage ->                        viewModel.onPageChanged(currentPage)                    }                    Spacer(modifier = Modifier.height(8.dp))                }                                stickyHeader {                    SearchBar(viewModel)                    Spacer(modifier = Modifier.height(16.dp))                }                item {                    ListScreen(viewModel.filteredItems.collectAsState().value)                }            }        }    )        if (isBottomSheetVisible) {        BottomSheetDialog(viewModel)    }}